sqls.sql:
select count(*)
from log l
left join resources r
on l.method=r.method


		and operator=#{operator_text}


		and time>=#{time1}


		and time<=#{time2}

select l.*,r.name as methodname
from log l
left join resources r
on l.method=r.method


		and operator=#{operator_text}


		and time>=#{time1}


		and time<=#{time2}


order by id desc
